parameters.param:
$list = @()
$list += new-object PSObject -property @{
Name = "Very Long Name 1";
Duration = "00:10:21.774"
}

$list += new-object PSObject -property @{
Name = "Long Name 2";
Duration = "10:10:21.774"
}

$list += new-object PSObject -property @{
Name = "Name 3";
Duration = "10:10:21.774"
}

$list += new-object PSObject -property @{
Name = "Total:";
Duration = "20:10:21.774"
}

$list | format-table -autoSize -property Name,Duration | out-string -stream | where-object { $_ }
